import React, { useState } from "react";
import "./app.css";

const PetPharmacy = () => {
  // Sample data for pet medicines
  const petMedicines = [
    { name: "HeartGard Plus", description: "Heartworm prevention for dogs", price: 39.99 },
    { name: "Advantage II", description: "Flea treatment for cats", price: 29.99 },
    { name: "Rimadyl", description: "Pain relief for dogs with arthritis", price: 45.49 },
    { name: "Revolution", description: "Topical solution for dogs and cats", price: 34.95 },
    // Add more medicines as needed
  ];

  // State to manage the cart
  const [cart, setCart] = useState([]);
  
  // Function to handle adding a medicine to the cart
  const handleAddToCart = (medicine) => {
    // Update the cart state
    setCart([...cart, medicine]);
    alert(`Added ${medicine.name} to the cart!`);
  };

  // Calculate the total amount
  const totalAmount = cart.reduce((total, medicine) => total + medicine.price, 0).toFixed(2);

  return (
    <div className="pet-pharmacy-container">
      <h2>Pet Pharmacy</h2>
      <div className="medicine-list">
        {petMedicines.map((medicine, index) => (
          <div key={index} className="medicine-item">
            <img src="https://placekitten.com/100/100" alt={medicine.name} />
            <div className="medicine-details">
              <h3>{medicine.name}</h3>
              <p>{medicine.description}</p>
              <p>Price: ${medicine.price.toFixed(2)}</p>
              <button onClick={() => handleAddToCart(medicine)}>Add to Cart</button>
            </div>
          </div>
        ))}
      </div>
      
      <div className="cart-container">
        <h3>Shopping Cart</h3>
        <ul>
          {cart.map((item, index) => (
            <li key={index}>{item.name} - ${item.price.toFixed(2)}</li>
          ))}
        </ul>
        <p>Total Amount: ${totalAmount}</p>
      </div>
    </div>
  );
};

export default PetPharmacy;
