import React, { useState } from 'react';
import './app.css';

const PetRoutine = () => {
  const [exercise, setExercise] = useState('');
  const [routineList, setRoutineList] = useState([]);

  const handleInputChange = (e) => {
    setExercise(e.target.value);
  };

  const handleAddRoutine = () => {
    if (exercise.trim() !== '') {
      setRoutineList((prevList) => [...prevList, exercise]);
      setExercise('');
    }
  };

  return (
    <div className="pet-routine-container">
      <h2>Pet Exercise Routines</h2>

      <div className="input-section">
        <input type="text" value={exercise} onChange={handleInputChange} placeholder="Enter exercise routine" />
        <button onClick={handleAddRoutine}>Add Routine</button>
      </div>

      <div className="routine-list">
        <h3>Routine List</h3>
        <ul>
          {routineList.map((routine, index) => (
            <li key={index}>{routine}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default PetRoutine;
