import React, { useState } from "react";
import "./app.css";

const PetPharmacy = () => {
  // Sample data for pet medicines
  const petMedicines = [
    { name: "Simparica Trio", description: "Chewable tablets for dogs - Flea, tick, and worm protection", price: 59.99 },
    { name: "Revolution Plus", description: "Topical solution for cats - Flea, tick, and worm prevention", price: 44.99 },
    { name: "Apoquel", description: "Tablets for dogs - Itch relief for allergic dermatitis", price: 34.49 },
    { name: "Bravecto Topical", description: "Topical solution for dogs - Flea and tick protection", price: 54.95 },
    { name: "Hill's Prescription Diet c/d Multicare", description: "Dry food for cats - Urinary health", price: 39.99 },
    { name: "Royal Canin Veterinary Diet Gastrointestinal Low Fat", description: "Dry food for dogs - Digestive health", price: 42.99 },
    // Add more medicines as needed
  ];

  const itemsPerPage = 2; // Number of medicines to display per page
  const [currentPage, setCurrentPage] = useState(1);
  const [cart, setCart] = useState([]);

  // Function to handle adding a medicine to the cart
  const handleAddToCart = (medicine) => {
    // Update the cart state
    setCart([...cart, medicine]);
    alert(`Added ${medicine.name} to the cart!`);
  };

  // Function to handle removing a medicine from the cart
  const handleRemoveFromCart = (index) => {
    const updatedCart = [...cart];
    updatedCart.splice(index, 1);
    setCart(updatedCart);
    alert(`Removed ${cart[index].name} from the cart!`);
  };

  // Calculate the total amount for items in the cart
  const cartTotalAmount = cart.reduce((total, item) => total + item.price, 0).toFixed(2);

  // Calculate the index range for the current page
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;

  // Get the medicines for the current page
  const currentPageMedicines = petMedicines.slice(startIndex, endIndex);

  return (
    <div className="pet-pharmacy-container">
      <h2>Pet Pharmacy</h2>
      <div className="medicine-list">
        {currentPageMedicines.map((medicine, index) => (
          <div key={index} className="medicine-item">
            <img src="https://placekitten.com/100/100" alt={medicine.name} />
            <div className="medicine-details">
              <h3>{medicine.name}</h3>
              <p>{medicine.description}</p>
              <p>Price: ${medicine.price.toFixed(2)}</p>
              <button onClick={() => handleAddToCart(medicine)}>Add to Cart</button>
            </div>
          </div>
        ))}
      </div>

      <div className="pagination">
        <button
          onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
          disabled={currentPage === 1}
        >
          {"<"} Prev
        </button>
        <span>Page {currentPage}</span>
        <button
          onClick={() => setCurrentPage((prev) => Math.min(prev + 1, Math.ceil(petMedicines.length / itemsPerPage)))}
          disabled={currentPage === Math.ceil(petMedicines.length / itemsPerPage)}
        >
          Next {">"}
        </button>
      </div>

      <div className="cart-container">
        <h3>Shopping Cart</h3>
        <ul>
          {cart.map((item, index) => (
            <li key={index}>
              {item.name} - ${item.price.toFixed(2)}
              <button onClick={() => handleRemoveFromCart(index)}>Remove from Cart</button>
            </li>
          ))}
        </ul>
        <p>Total Amount in Cart: ${cartTotalAmount}</p>
      </div>
    </div>
  );
};

export default PetPharmacy;
