import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import Calendar from "react-calendar";
import Modal from "react-modal";

const Doctor = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [previousAppointments, setPreviousAppointments] = useState([]);
  const [petHealthHistory, setPetHealthHistory] = useState("");
  const [showAppointmentHistory, setShowAppointmentHistory] = useState(false);
  const [showHealthHistory, setShowHealthHistory] = useState(false);
  const history = useHistory();

  const handleDateChange = (date) => {
    setSelectedDate(date);
    fetchAppointmentsAndHealthHistory(date);
  };

  const fetchAppointmentsAndHealthHistory = (date) => {
    // Simulated data fetching
    const fakeAppointments = [
      { time: "10:00 AM", reason: "Checkup" },
      { time: "2:30 PM", reason: "Vaccination" },
    ];

    const fakePetHealthHistory =
      "No significant health issues. Regular checkups and vaccinations are up to date.";

    // Update state with fetched data
    setPreviousAppointments(fakeAppointments);
    setPetHealthHistory(fakePetHealthHistory);
  };

  const scheduleAppointment = () => {
    // Simulated appointment scheduling
    console.log(`Appointment scheduled for: ${selectedDate}`);
    // You can add real logic here, such as sending a request to a server.

    // Navigate back to the main page after scheduling
    history.push("/");
  };

  const openAppointmentHistoryModal = () => {
    setShowAppointmentHistory(true);
  };

  const closeAppointmentHistoryModal = () => {
    setShowAppointmentHistory(false);
  };

  const openHealthHistoryModal = () => {
    setShowHealthHistory(true);
  };

  const closeHealthHistoryModal = () => {
    setShowHealthHistory(false);
  };

  return (
    <div>
      <h2>Schedule an Appointment</h2>
      <Calendar onChange={handleDateChange} value={selectedDate} />
      <p>Selected Date: {selectedDate.toDateString()}</p>

      <div>
        <button onClick={openAppointmentHistoryModal}>Appointment History</button>
        <button onClick={openHealthHistoryModal}>Pet Health History</button>
      </div>

      {/* Additional UI for appointment history and pet health history */}
      <Modal
        isOpen={showAppointmentHistory}
        onRequestClose={closeAppointmentHistoryModal}
        contentLabel="Appointment History Modal"
      >
        <h3>Appointment History</h3>
        {previousAppointments.length > 0 ? (
          <ul>
            {previousAppointments.map((appointment, index) => (
              <li key={index}>
                <strong>Time:</strong> {appointment.time},{" "}
                <strong>Reason:</strong> {appointment.reason}
              </li>
            ))}
          </ul>
        ) : (
          <p>No previous appointments for the selected date.</p>
        )}
        <button onClick={closeAppointmentHistoryModal}>Close</button>
      </Modal>

      <Modal
        isOpen={showHealthHistory}
        onRequestClose={closeHealthHistoryModal}
        contentLabel="Pet Health History Modal"
      >
        <h3>Pet Health History</h3>
        <p>{petHealthHistory}</p>
        <button onClick={closeHealthHistoryModal}>Close</button>
      </Modal>

      <button onClick={scheduleAppointment}>Schedule Appointment</button>
    </div>
  );
};

export default Doctor;
